clear;clc;# 計算 u = v =0.5 時的機略n = 10;k = 5;u = 0.5;q9_prob = nchoosek(n,k) * (u) ^ 5 * (1-u) ^ 5;fprintf ("question 9 prob = %f\n", q9_prob);# 計算 u = v = 0.9 時的機略u = 0.9; k = 10 * u;q10_prob = nchoosek(n,k) * (u) ^ (k) * (1-u) ^ (n-k);fprintf ("question 10 prob = %f\n", q10_prob);# question 12 Hoeffding Inequality# P (|v-u| > epsolion) <=  2 * exp(-2 * n * epsolion ^ 2)# v <= 0.1 , u = 0.9 #  --> v = 0.1 ; v = 0#  --> v - u = -0.8 or - 0.9# epsolion = 0.8;  q12_prob = 2 * exp( - 2 * n * epsolion ^ 2);fprintf ("question 12 hoffding inequality probility for u = 0.9 and v <= 0.1 : %.10f\n", q12_prob); 